import UIKit
import SnapKit

class TermsofPolicyView: UIView {
    let scrolView = UIScrollView()
    
    let titleLabel: UILabel = {
        let label = UILabel()
        label.text = "Пользовательское соглашение на использование программы Pillikan для Мобильных устройств"
        label.font = UIFont.semibold16
        label.numberOfLines = 3
        label.textColor = .pilicanGray
        label.textAlignment = .center
        return label
    }()
    
    let textView: UITextView = {
        let textView = UITextView()
        textView.layer.masksToBounds = true
        textView.font = UIFont.book14
        textView.textColor = .pilicanLightGray
        textView.backgroundColor = .clear
        textView.text = termsText
        textView.isScrollEnabled = false
        textView.isEditable = false
        textView.isSelectable = false
        return textView
    }()
    private let contentView = UIView()
    
    override init(frame: CGRect) {
        super.init(frame: frame)
        setupLayouts()
        configureView()
    }
    
    
    required init?(coder: NSCoder) {
        fatalError("init(coder:) has not been implemented")
    }
    
    private func setupLayouts() {
        addSubview(contentView)
        contentView.snp.makeConstraints { $0.edges.equalToSuperview() }
        contentView.addSubview(scrolView)
        scrolView.snp.makeConstraints { (make) in
            make.top.bottom.equalToSuperview()
            make.leading.trailing.equalToSuperview().inset(10)
        }
        scrolView.alwaysBounceVertical = true
        scrolView.showsHorizontalScrollIndicator = false
        
        scrolView.addSubview(titleLabel)
        titleLabel.snp.makeConstraints { (make) in
            make.left.right.equalToSuperview().inset(20)
            make.top.equalToSuperview().inset(35)
        }
        
        scrolView.addSubview(textView)
        textView.snp.makeConstraints { (make) in
            make.top.equalTo(titleLabel.snp.bottom).offset(20)
            make.left.right.bottom.equalToSuperview().inset(5)
            make.width.equalToSuperview().inset(5)
        }
    }
    
    private func configureView() {
        scrolView.bounces = false
    }
}
